/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `tr_users_after_insert` AFTER INSERT ON `users` FOR EACH ROW BEGIN
    DECLARE parent_level INT DEFAULT 0;
    DECLARE parent_path VARCHAR(1000) DEFAULT '';
    DECLARE new_path VARCHAR(1000);
    
    -- Get parent information if referrer exists
    IF NEW.referrer_id IS NOT NULL THEN
        SELECT level, path 
        INTO parent_level, parent_path
        FROM user_mlm_tree 
        WHERE user_id = NEW.referrer_id;
    END IF;
    
    -- Create the path for the new user
    IF parent_path = '' OR parent_path IS NULL THEN
        SET new_path = CONCAT('/', NEW.id, '/');
    ELSE
        SET new_path = CONCAT(parent_path, NEW.id, '/');
    END IF;
    
    -- Insert new user into MLM tree
    INSERT INTO user_mlm_tree (
        user_id,
        parent_id,
        level,
        path,
        direct_referrals,
        total_team_size,
        active_team_size,
        team_business
    ) VALUES (
        NEW.id,
        NEW.referrer_id,
        parent_level + 1,
        new_path,
        0,
        0,
        0,
        0.00
    );
    
    -- Update parent's direct referrals count
    IF NEW.referrer_id IS NOT NULL THEN
        UPDATE user_mlm_tree 
        SET direct_referrals = direct_referrals + 1
        WHERE user_id = NEW.referrer_id;
        
        -- Update total team size for all ancestors
        UPDATE user_mlm_tree 
        SET total_team_size = total_team_size + 1
        WHERE user_id IN (
            SELECT DISTINCT SUBSTRING_INDEX(SUBSTRING_INDEX(new_path, '/', numbers.n), '/', -1) as ancestor_id
            FROM (
                SELECT 2 as n UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL 
                SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9 UNION ALL SELECT 10
            ) numbers
            WHERE CHAR_LENGTH(new_path) - CHAR_LENGTH(REPLACE(new_path, '/', '')) >= numbers.n
            AND SUBSTRING_INDEX(SUBSTRING_INDEX(new_path, '/', numbers.n), '/', -1) != ''
            AND SUBSTRING_INDEX(SUBSTRING_INDEX(new_path, '/', numbers.n), '/', -1) != NEW.id
        );
        
        -- Update active team size for all ancestors if new user is active
        IF NEW.status = 'active' THEN
            UPDATE user_mlm_tree 
            SET active_team_size = active_team_size + 1
            WHERE user_id IN (
                SELECT DISTINCT SUBSTRING_INDEX(SUBSTRING_INDEX(new_path, '/', numbers.n), '/', -1) as ancestor_id
                FROM (
                    SELECT 2 as n UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL 
                    SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9 UNION ALL SELECT 10
                ) numbers
                WHERE CHAR_LENGTH(new_path) - CHAR_LENGTH(REPLACE(new_path, '/', '')) >= numbers.n
                AND SUBSTRING_INDEX(SUBSTRING_INDEX(new_path, '/', numbers.n), '/', -1) != ''
                AND SUBSTRING_INDEX(SUBSTRING_INDEX(new_path, '/', numbers.n), '/', -1) != NEW.id
            );
        END IF;
    END IF;
    
END */;;
