/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `tr_users_after_update` AFTER UPDATE ON `users` FOR EACH ROW BEGIN
    DECLARE user_path VARCHAR(1000);
    
    -- Handle status changes that affect active team size
    IF OLD.status != NEW.status THEN
        SELECT path INTO user_path 
        FROM user_mlm_tree 
        WHERE user_id = NEW.id;
        
        -- User became active
        IF OLD.status != 'active' AND NEW.status = 'active' THEN
            UPDATE user_mlm_tree 
            SET active_team_size = active_team_size + 1
            WHERE user_id IN (
                SELECT DISTINCT SUBSTRING_INDEX(SUBSTRING_INDEX(user_path, '/', numbers.n), '/', -1) as ancestor_id
                FROM (
                    SELECT 2 as n UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL 
                    SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9 UNION ALL SELECT 10
                ) numbers
                WHERE CHAR_LENGTH(user_path) - CHAR_LENGTH(REPLACE(user_path, '/', '')) >= numbers.n
                AND SUBSTRING_INDEX(SUBSTRING_INDEX(user_path, '/', numbers.n), '/', -1) != ''
                AND SUBSTRING_INDEX(SUBSTRING_INDEX(user_path, '/', numbers.n), '/', -1) != NEW.id
            );
        END IF;
        
        -- User became inactive
        IF OLD.status = 'active' AND NEW.status != 'active' THEN
            UPDATE user_mlm_tree 
            SET active_team_size = active_team_size - 1
            WHERE user_id IN (
                SELECT DISTINCT SUBSTRING_INDEX(SUBSTRING_INDEX(user_path, '/', numbers.n), '/', -1) as ancestor_id
                FROM (
                    SELECT 2 as n UNION ALL SELECT 3 UNION ALL SELECT 4 UNION ALL SELECT 5 UNION ALL 
                    SELECT 6 UNION ALL SELECT 7 UNION ALL SELECT 8 UNION ALL SELECT 9 UNION ALL SELECT 10
                ) numbers
                WHERE CHAR_LENGTH(user_path) - CHAR_LENGTH(REPLACE(user_path, '/', '')) >= numbers.n
                AND SUBSTRING_INDEX(SUBSTRING_INDEX(user_path, '/', numbers.n), '/', -1) != ''
                AND SUBSTRING_INDEX(SUBSTRING_INDEX(user_path, '/', numbers.n), '/', -1) != NEW.id
            );
        END IF;
    END IF;
    
END */;;
